	if( !function_exists('traveltour_gdlr_core_before_import') ){
		function traveltour_gdlr_core_before_import(){
			$custom_taxs = '{"tour-age":{"label":"Tour Age","hierarchical":true},"tour-activity":{"label":"Activity","hierarchical":true},"tour-destination":{"label":"Destination","hierarchical":true}}';
			update_option('tourmaster_custom_tour_taxs', json_decode($custom_taxs, true));

			if( function_exists('tourmaster_add_custom_tour_tax') ){
				tourmaster_add_custom_tour_tax();
			}
		}
	}
	add_action('gdlr_core_after_succuessful_import', 'traveltour_gdlr_core_after_succuessful_import');
	if( !function_exists('traveltour_gdlr_core_after_succuessful_import') ){
		function traveltour_gdlr_core_after_succuessful_import( $slug = '' ){
			if( $slug == 'citytour' ){
				$tax_meta = '{"tour_category":[],"tour_tag":[],"tour-age":[],"tour-activity":{"attraction-tickets":"5239","transportation-passes":"5245","sightseeing":"5243","museum-tickets":"5241","outdoor-activities":"5242","unexpected-journeys":"5240","amusement-parks":"5238","threatre-concert":"5244"},"tour-destination":{"marrakech":"4609","new-york":"4592","tokyo":"4501","paris":"4630","london":"4510","rio-de-janeiro":"4495"}}';
			}else{
				$tax_meta = '{"tour_category":[],"tour_tag":[],"tour-age":[],"tour-activity":[],"tour-destination":{"africa":"4609","america":"4592","asia":"4501","eastern-europe":"4630","europe":"4510","south-america":"4495"}}';
			}
			$tax_meta_options = json_decode($tax_meta, true);
			foreach( $tax_meta_options as $tax_slug => $tax_options ){
				foreach( $tax_options as $term_slug => $term_val ){
					$term = get_term_by('slug', $term_slug, $tax_slug);
					update_term_meta($term->term_id, 'thumbnail', $term_val);
				}
			}
		}
	}

